const burgerBtn=document.querySelector(".header__body svg"),burger=document.querySelector(".burger"),burgerClose=burger.querySelector("svg"),overlay=document.querySelector(".overlay");burgerBtn.addEventListener("click",e=>{burger.classList.add("burger--active"),overlay.classList.add("overlay--active")}),burgerClose.addEventListener("click",e=>{burger.classList.remove("burger--active"),overlay.classList.remove("overlay--active")}),overlay.addEventListener("click",e=>{burger.classList.remove("burger--active"),overlay.classList.remove("overlay--active")});const swiper=new Swiper(".swiper-container",{direction:"horizontal",loop:!0,speed:1200,slidesPerView:1,autoplay:{delay:1e3,disableOnInteraction:!1},pagination:{el:".swiper-pagination",clickable:!0}}),swiperProducts=new Swiper(".products-swiper",{direction:"horizontal",loop:!0,speed:600,spaceBetween:10,autoplay:{delay:600,disableOnInteraction:!1},breakpoints:{0:{slidesPerView:3},575:{slidesPerView:5},767:{slidesPerView:7},991:{slidesPerView:9},1250:{slidesPerView:10}}}),tabBody=document.querySelectorAll(".tab__body"),tabBtn=document.querySelectorAll(".tab__btn");tabBtn.forEach(e=>{e.addEventListener("click",r=>{tabBtn.forEach(e=>{e.classList.remove("tab__btn--active")}),e.classList.add("tab__btn--active"),tabBody.forEach(e=>{e.classList.remove("tab__body--active")}),document.querySelector(`div[data-tab="${e.dataset.tab}"]`).classList.add("tab__body--active")})});